#!/usr/bin/env bash
gcloud beta container --project "deepvideoanalytics-161408" clusters create "dvacluster" --zone "us-central1-a" --username="admin" --cluster-version "1.7.8-gke.0" --machine-type "custom-4-8192" --image-type "COS" --disk-size "100" --scopes "https://www.googleapis.com/auth/compute","https://www.googleapis.com/auth/devstorage.read_write","https://www.googleapis.com/auth/logging.write","https://www.googleapis.com/auth/monitoring","https://www.googleapis.com/auth/servicecontrol","https://www.googleapis.com/auth/service.management.readonly","https://www.googleapis.com/auth/trace.append" --preemptible --num-nodes "3" --network "default" --enable-cloud-logging --no-enable-cloud-monitoring --subnetwork "default" --enable-autoscaling --enable-legacy-authorization --min-nodes "2" --max-nodes "5" --enable-autoupgrade